import React, { useEffect, useState } from 'react';
import "./Navbar.css";
import healthReport from "../../assets/285-2859467_medical-record.jpeg";
import { useDispatch, useSelector } from 'react-redux';
import { loadAccount, loadProvider } from '../../store/interactions';
import Blockies from "react-blockies";

const Navbar = () => {
    const dispatch = useDispatch();
    const provider = useSelector((state) => state.provider.connection);
    const account = useSelector((state) => state.provider.account);
    const balance = useSelector((state) => state.provider.balance);

    const [showDropdown, setShowDropdown] = useState(false);

    // Ensure provider is loaded and accounts are fetched
    useEffect(() => {
        const initializeProvider = async () => {
            if (!provider) {
                await loadProvider(dispatch);
            }
            await loadAccount(provider, dispatch);
        };
        initializeProvider();
    }, [dispatch, provider]);

    const connectHandler = async () => {
        let providerInstance = provider;
        if (!providerInstance) {
            providerInstance = loadProvider(dispatch);
        }
        await loadAccount(providerInstance, dispatch);
    };

    const networkHandler = async (e) => {
        try {
            await window.ethereum.request({
                method: "wallet_switchEthereumChain",
                params: [{ chainId: e.target.value }],
            });
        } catch (error) {
            console.error('Failed to switch network:', error);
        }
    };

    const logoutHandler = () => {
        // Logic to handle logout, e.g., disconnecting the wallet
        setShowDropdown(false);
        console.log('Logged out');
    };

    const toggleDropdown = () => {
        setShowDropdown(!showDropdown);
    };

    return (
        <div className="Navbar">
            <div className="navname">
                <img src={healthReport} alt="MedLock Logo" />
                <h2>E-Report</h2>
            </div>
            <div className="navnetworkSelector">
                <select name="network" id="network" onChange={networkHandler}>
                    <option value="0" disabled>Select Network</option>
                    <option value="0x7A69">Localhost</option>
                    <option value="0xAA36A7">Sepolia</option>
                </select>
            </div>
            <div className="navbalance">
                {balance && (
                    <p className="navmyBalance">
                        <small>My Balance: </small>
                        {Number(balance).toFixed(2)} ETH
                    </p>
                )}
                {account ? (
                    <div className="navAccountDropdown">
                        <button className="navmyAccount" onClick={toggleDropdown}>
                            <Blockies
                                seed={account}
                                size={8}
                                scale={3}
                                color="#2187D0"
                                bgColor="#F1F2F9"
                                spotColor="#767f92"
                                className="identicon"
                            />
                            {account.slice(0, 5) + "..." + account.slice(38, 42)}
                        </button>
                        {showDropdown && (
                            <div className="dropdown-menu">
                                <button onClick={logoutHandler}>Log Out</button>
                            </div>
                        )}
                    </div>
                ) : (
                    <button className="navbalance-box" onClick={connectHandler}>
                        Connect Wallet
                    </button>
                )}
            </div>
        </div>
    );
};

export default Navbar;
